function escrever(e,n){var r=e.split("").reverse(),t=setInterval(function(){if(!r.length)return clearInterval(t),setTimeout();var e=r.pop();div.innerHTML+=e},100)}function limpar(e){var n=div.innerHTML,r=n.length,t=setInterval(function(){return 0==r--?(clearInterval(t),e()):void(div.innerHTML=n.slice(0,r))},100)}function rodape(e,n){function r(n){t<e.length-1?t++:t=0;var i=e[t];escrever(i,function(){limpar(r)})}var t=-1;r(r)}var div=document.getElementById("log"),textos=["Creative Designer & Front-end Developer"];rodape(textos);